import React, { Component } from 'react';

class AppChild extends Component {

    constructor(props){
        super(props);
this.state = {
    USA: false,
    France: false,

};
this.handleInputChange = this.handleInputChange.bind(this);
}
handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

  }

    render (){
        return (
        <div>
            <form>
            <label>Select A Country</label><br/>
            <label>USA<input type='checkbox' checked={this.state.USA} name='USA' onChange={this.handleInputChange}/>
            </label>
            <label>France<input type='checkbox' checked={this.state.France} name='France' onChange={this.handleInputChange}/>
            </label>
            </form>
        </div>
        );
}
}

export default AppChild;
//checkbox. input w/ type, checked property T/F based on state
